import React from 'react';
import './App.css';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      response: "",
      clicked: true
    }
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch(`http://localhost:5000`, {
      method: 'GET'
    })
    .then(res => res.json())
    .then(resJSON => {
      this.setState({
        response: resJSON
      })
    })
  }

  handleClick(e) {
    e.preventDefault();
    this.setState({
      clicked: !this.state.clicked
    })
  }

  handleReload(e) {
    e.preventDefault();
    window.location.reload(false);
  }

  errorClick(e) {
    e.preventDefault();
    fetch(`http://localhost:5000/api/click`, {
      method: 'GET'
    })
  }

  render() {
    return (
      <div>
        <div className='landing'>
          <h1>reactdog</h1>
          <p>{this.state.response.message}</p>
          <button onClick={this.handleClick}>Click me</button>
          {this.state.clicked === true ? <p>Click that button</p> : <p>Button was clicked</p>}
          <button onClick={this.handleReload}>Reload page</button>
          <button onClick={this.errorClick}>400 error</button>
        </div>
      </div>
    )
  }
}
