import React from 'react';
import { Link } from 'react-router-dom';
import './landing.css'
import Logo from '../../assets/reactdog.png'

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      response: "",
      clicked: true
    }
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch(`http://localhost:5000`, {
      method: 'GET'
    })
    .then(res => res.json())
    .then(resJSON => {
      this.setState({
        response: resJSON
      })
    })
  }

  handleClick(e) {
    e.preventDefault();
    this.setState({
      clicked: !this.state.clicked
    })
  }

  handleReload(e) {
    e.preventDefault();
    window.location.reload(false);
  }

  errorFourClick(e) {
    e.preventDefault();
    fetch(`http://localhost:5000/api/four`, {
      method: 'GET'
    })
  }

  errorFiveClick(e) {
    e.preventDefault();
    fetch(`http://localhost:5000/api/five`, {
      method: 'GET'
    })
  }

  render() {
    return (
      <div>
        <div className='landing'>
          <img src={Logo} alt='reactdog' className='logo'/>
          <h1>REACTDOG</h1>
          <p>{this.state.response.message}</p>


          <div className='server-error test'>
            <h3>CLICK SIMULATOR</h3>
            <div>
            <button onClick={this.handleClick}>Click me</button>
            {this.state.clicked === true ? <p>Click that button</p> : <p>Button was clicked</p>}
            </div>
          </div>
          <div className='server-error'>
            <h3>NAVIGATION SIMULATOR</h3>
            <button onClick={this.handleReload}>Reload page</button>
            <Link to='/test'><button>Test page</button></Link>
          </div>
          <div className='server-error'>
            <h3>SERVER ERROR SIMULATOR</h3>
            <button onClick={this.errorFourClick}>400 error</button>
            <button onClick={this.errorFiveClick}>500 error</button>
          </div>
        </div>
      </div>
    )
  }
}
